CREATE SEQUENCE meetcoin_trade_reward_activity_id_sequence
INCREMENT 1
MINVALUE 1000
MAXVALUE 99999999
START 1001;

CREATE TABLE IF NOT EXISTS "trade_reward_activity" (
  platform VARCHAR(32) NOT NULL,
  country VARCHAR(32) NOT NULL,
  act_id int4 NOT NULL DEFAULT nextval('meetcoin_trade_reward_activity_id_sequence'),
  active SMALLINT NOT NULL DEFAULT 1,
  offers JSONB NOT NULL,
  tutorial_links JSONB,
  commission_rate numeric(8,2) NOT NULL DEFAULT 1,
  reward_limit int4 NOT NULL DEFAULT 1500,
  create_ts BIGINT NOT NULL DEFAULT ROUND(EXTRACT(EPOCH FROM current_timestamp) * 1000),
  update_ts BIGINT NOT NULL DEFAULT ROUND(EXTRACT(EPOCH FROM current_timestamp) * 1000),
  PRIMARY KEY ("platform","country")
);
COMMENT ON TABLE "trade_reward_activity" is 'trade活动配置';

COMMENT ON COLUMN "trade_reward_activity".platform is '客户端平台';
COMMENT ON COLUMN "trade_reward_activity".country is '配置所属国家二字大写代号';
COMMENT ON COLUMN "trade_reward_activity".act_id is '活动自增编号';
COMMENT ON COLUMN "trade_reward_activity".active is '活动是否上架';
COMMENT ON COLUMN "trade_reward_activity".offers is '额外奖励任务';
COMMENT ON COLUMN "trade_reward_activity".tutorial_links is '教程视频';
COMMENT ON COLUMN "trade_reward_activity".commission_rate is '佣金返现系数';
COMMENT ON COLUMN "trade_reward_activity".reward_limit is '单人奖励上限';
COMMENT ON COLUMN "trade_reward_activity".create_ts is '创建时间';
COMMENT ON COLUMN "trade_reward_activity".update_ts is '更新时间';


